const timer=document.querySelector(".timer"),startbutton=document.getElementById("start"),stopbutton=document.getElementById("stop"),refreshbutton=document.getElementById("refresh"),toursButton=document.getElementById("tour"),listtours=document.querySelector(".list");let second=0,intervalId=null;function formatTime(t){return`${String(Math.floor(t/60)).padStart(2,"0")}:${String(t%60).padStart(2,"0")}`}function updateDisplay(){timer.textContent=formatTime(second)}startbutton.addEventListener("click",(()=>{null===intervalId&&(intervalId=setInterval((()=>{second++,updateDisplay()}),1e3))})),stopbutton.addEventListener("click",(()=>{clearInterval(intervalId),intervalId=null})),refreshbutton.addEventListener("click",(()=>{clearInterval(intervalId),intervalId=null,second=0,updateDisplay(),listtours.textContent=""})),toursButton.addEventListener("click",(()=>{const t=document.createElement("li");t.textContent=formatTime(second),listtours.appendChild(t)}));